import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import { ScrollView, TextInput } from 'react-native-gesture-handler';
import { FontAwesome } from '@expo/vector-icons';
import { useRoute } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';

import styles from './estilo';

import { HeaderRequisicao, DadosUsuario } from '../../AuthContext';

function tConfiguracao() {

    const navigation = useNavigation();

    function Avancao() {
        navigation.navigate('tConfirmarSenha');
    }

    const route = useRoute();

    const [nome, setnome] = useState(route.params?.nome);
    const [sobrenome, setsobrenome] = useState(route.params?.sobrenome);
    const [celular, setcelular] = useState(route.params?.celular);
    const [email, setemail] = useState(route.params?.email);
    const [preco, setpreco] = useState(route.params?.preco);

    const [cidade, setcidade] = useState(route.params?.localidade);

    const [descricao, setdescricao] = useState("");
    const [imagem, setimagem] = useState(""); 

    async function MostrarUsuario() {
        const userData = await DadosUsuario();
        const headers = await HeaderRequisicao();
        const url = `https://localhost:44396/api/ConfigUsu?id=${userData.ID}`;

        fetch(url, {
            method: 'GET',
            headers
        })
            .then((response) => response.json())
            .then((usuario) => {
                console.log(usuario)
                setimagem(usuario.imagem)
                setnome(usuario.nome)
                setsobrenome(usuario.sobrenome)
                setemail(usuario.email)
                setcidade(usuario.cidade)
                setcelular(usuario.celular)
                setpreco(usuario.preco)
                setdescricao(usuario.descricao)
            })
            .catch((error) => {
                console.log(error);
                alert('Erro ao inserir informações');
            });
    }

    useEffect(() => {
        MostrarUsuario();
    }, []);

    return (

        <ScrollView>
            <View style={styles.container}>
                <Text style={styles.title}>Olá, Usuário!</Text>
                <View style={styles.view}>
                    <View style={styles.editor}>
                        <FontAwesome
                            name="pencil-square-o"
                            size={24}
                            color="black"
                            onPress={Avancao}
                        />
                    </View>
                    <Text style={styles.text}>Nome</Text>
                    <TextInput style={styles.input}
                        value={nome}
                    />

                    <Text style={styles.text}>Sobrenome</Text>
                    <TextInput
                        style={styles.input}
                        value={sobrenome}
                    />

                    <Text style={styles.text}>Email</Text>
                    <TextInput
                        style={styles.input}
                        value={email}
                    />

                    <Text style={styles.text}>Cidade</Text>
                    <TextInput
                        style={styles.input}
                        value={cidade}
                    />

                    <Text style={styles.text}>Celular</Text>
                    <TextInput
                        style={styles.input}
                        value={celular}
                    />

                    <Text style={styles.text}>Preço</Text>
                    <TextInput
                        style={styles.input}
                        value={preco}
                    />

                    <Text style={styles.text}>Descrição</Text>
                    <TextInput
                        style={styles.inputDesc}
                        value={descricao}
                        multiline
                    />

                </View>
            </View >
        </ScrollView>

    );
}

export default tConfiguracao;
