import React, { useState } from 'react';
import { View, StyleSheet, Image, TouchableOpacity, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/FontAwesome';
import DefaultImage from '../../assets/home-background@2x.png';

function CardUsuario({ usuario }) {
  const navigation = useNavigation();
  const [favorito, setFavorito] = useState(false);
  const [classificacao, setClassificacao] = useState(0);

  const enviarParaBancoDeDados = (method) => {
    const cuidadorId = usuario.id; // ID do cuidador
    const usuarioId = 1; // ID do usuário do aplicativo (você pode substituir pelo valor correto)
    const url = 'https://localhost:44396/api/favoritosusu';

    const requestOptions = {
      method: method,
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        usuario_id: usuarioId,
        cuidador_id: cuidadorId,
      }),
    };

    if (method === 'DELETE') {
      // Adicione o ID do favorito na URL de exclusão
      fetch(`https://localhost:44396/api/favoritosusu?/id=@id`, requestOptions)
        .then((response) => {
          if (response.ok) {
            // Sucesso ao excluir os dados da API
            console.log('Dados excluídos com sucesso da API');
          } else {
            // Erro ao excluir os dados da API
            throw new Error('Erro ao excluir os dados da API');
          }
        })
        .catch((error) => {
          console.log(error);
          alert('Erro ao excluir informações');
        });
    } else {
      fetch(url, requestOptions)
        .then((response) => {
          if (response.ok) {
            // Sucesso ao enviar os dados para a API
            console.log('Dados enviados com sucesso para a API');
          } else {
            // Erro ao enviar os dados para a API
            throw new Error('Erro ao enviar os dados para a API');
          }
        })
        .catch((error) => {
          console.log(error);
          alert('Erro ao inserir informações');
        });
    }
  };

  const handleToggleFavorito = () => {
    if (favorito) {
      setFavorito(false);
      enviarParaBancoDeDados('DELETE');
    } else {
      setFavorito(true);
      enviarParaBancoDeDados('POST');
    }
  };

  const renderEstrelas = () => {
    return [1, 2, 3, 4, 5].map((index) => (
      <TouchableOpacity
        key={index}
        onPress={() => setClassificacao(index)}
        style={styles.estrela}
      >
        <Icon
          name={index <= classificacao ? 'star' : 'star-o'}
          size={18}
          color="gold"
        />
      </TouchableOpacity>
    ));
  };

  const abrirDetalhes = () => {
    navigation.navigate('tDetalhes', { usuario });
  };

  return (
    <TouchableOpacity style={styles.cardContainer} onPress={abrirDetalhes}>
      <View style={styles.imageContainer}>
        <Image
          source={usuario.Imagem ? { uri: usuario.Imagem } : DefaultImage}
          defaultSource={DefaultImage}
          style={styles.image}
          resizeMode="cover"
        />
        <View style={styles.estrelasContainer}>{renderEstrelas()}</View>
      </View>
      <View style={styles.infoContainer}>
        <Text style={styles.nameText}>{usuario.nome}</Text>
        <Text style={styles.distanciaText}>Estado: {usuario.estado}</Text>
        <Text style={styles.precoText}>Preço por dia: R$ {usuario.preco}</Text>
        <View style={styles.iconsContainer}>
          <TouchableOpacity onPress={handleToggleFavorito}>
            <Icon name="heart" size={18} color={favorito ? 'red' : 'grey'} />
          </TouchableOpacity>
          <View style={styles.tipo}>
            <Text style={styles.tipoText}>{usuario.tipo}</Text>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cardContainer: {
    width: '90%',
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: 'white',
    borderRadius: 15,
    marginVertical: 10,
    marginLeft: '5%',
    marginRight: '5%',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'row',
  },
  imageContainer: {
    marginRight: 10,
  },
  image: {
    borderWidth: 2,
    borderColor: 'black',
    borderRadius: 100,
    width: 120,
    height: 120,
    overflow: 'hidden',
  },
  estrelasContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 10,
  },
  estrela: {
    marginLeft: 5,
  },
  infoContainer: {
    flex: 1,
    marginLeft: 10,
  },
  iconsContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 5,
  },
  nameText: {
    fontSize: 18,
    fontFamily: 'Ubuntu_700Bold',
    marginBottom: 5,
  },
  distanciaText: {
    fontSize: 15,
    marginBottom: 5,
  },
  precoText: {
    fontSize: 15,
    marginBottom: 5,
  },
  tipo: {
    paddingHorizontal: 10,
    paddingVertical: 5,
    backgroundColor: 'green',
    borderRadius: 5,
    marginLeft: 5,
  },
  tipoText: {
    color: 'white',
  },
});

export default CardUsuario;
