import React, { useEffect, useState } from 'react';
import { View, ScrollView } from 'react-native';

import { HeaderRequisicao, DadosUsuario } from '../../AuthContext';
import CardUsuario from '../../components/Cards/CardRecente';
import styles from '../tRecentes/estilo';

function ListaDeRecentes({ searchTerm }) {
  const [recentes, setRecentes] = useState([]);

  async function ListarFavoritos() {
    const headers = await HeaderRequisicao();
    fetch('https://localhost:44396/api/favoritosusu', {
      method: 'GET', 
      headers
    })
      .then((response) => response.json())
      .then((json) => {
        const uniqueUsuarios = json.reduce((unique, favorito) => {
          if (!unique.some((item) => item.cuidador.id === favorito.cuidador.id)) {
            unique.push(favorito);
          }
          return unique;
        }, []);

        setUsuarios(uniqueUsuarios);
      })
      .catch((error) => {
        console.log(error);
        alert('Erro ao inserir informações');
      });
  }

  useEffect(() => {
    ListarRecentes();
  }, []);

  return (
    <ScrollView>
      <View style={styles.container}>
        {recentes.map((recente) => (
          <CardUsuario
            usuario={recente.cuidador}
            key={recente.id}
            favorito={false} // Definindo o favorito como false para manter o coração vazio
          />
        ))}
      </View>
    </ScrollView>
  );
}

export default ListaDeRecentes;
