import React, { useContext, useState } from 'react';
import { View, Image, ImageBackground, Text, TextInput, TouchableOpacity } from 'react-native';
import { RectButton } from 'react-native-gesture-handler';
import { useNavigation } from '@react-navigation/native';
import { AuthContext } from '../../AuthContext';

import styles from './estilo';
import styleGeral from '../estiloGeral/style';

export default function tLogin() {
  const navigation = useNavigation();
  const { SalvarJWT, ChecarLoginUsuario } = useContext(AuthContext);
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  function Avancao() {
    // Realizar a autenticação e salvar o token JWT
    const token = '...'; // Obtenha o token JWT da autenticação

    SalvarJWT(token);

    // Verificar se o usuário já está logado
    const usuarioLogado = ChecarLoginUsuario();
    if (!usuarioLogado) {
      // Redirecionar para a página de login
      navigation.navigate('Login');
      return;
    }

    // Continuar com a navegação para a próxima página
    navigation.navigate('Routes2');
  }

  function Esqueci() {
    navigation.navigate('tEsqueciSenha');
  }

  function Criar() {
    navigation.navigate('tSelecaoUser');
  }

  return (
    <ImageBackground style={styleGeral.container}>
      <View style={styles.main}>
        <Image style={{ width: 207, height: 225, marginTop: 200, marginLeft: 60 }}
          source={require('../../assets/logo.png')} />
      </View>

      <View style={styles.footer}>
        <TextInput
          style={styleGeral.input}
          placeholder="Digite seu email"
          value={email}
          onChangeText={setEmail}
        />
        <TextInput
          style={styleGeral.input}
          placeholder="Digite sua senha"
          autoCorrect={false}
          secureTextEntry
          value={senha}
          onChangeText={setSenha}
        />

        <RectButton style={styleGeral.button} onPress={Avancao}>
          <Text style={styleGeral.buttonText}>Entrar</Text>
        </RectButton>
      </View>

      <View style={styles.main}>
        <Text style={styles.title}>Esqueci sua senha?
          <TouchableOpacity onPress={Esqueci}>
            <Text style={styles.hyperlinkStyle}>Clique aqui</Text>
          </TouchableOpacity>
        </Text>

        <Text style={styles.title}>Não possui uma conta?
          <RectButton onPress={Criar}>
            <Text style={styles.hyperlinkStyle}>Clique aqui</Text>
          </RectButton>
        </Text>
      </View>
    </ImageBackground>
  );
}
