import React, { useState } from 'react';
import { View, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/FontAwesome';

function CardUsuario({ usuario }) {
  const navigation = useNavigation();
  const [favorito, setFavorito] = useState(false);
  const [classificacao, setClassificacao] = useState(0);

  function abrirDetalhes() {
    navigation.navigate('tDetalhes', { usuario });
  }

  function enviarParaBancoDeDados() {
    if (favorito) {
      // Aqui você pode fazer a lógica para enviar os dados para o banco de dados
      console.log('Enviar para o banco de dados:', usuario);
    } else {
      // Aqui você pode fazer a lógica para excluir os dados do banco de dados
      console.log('Excluir do banco de dados:', usuario);
    }
  }

  return (
    <TouchableOpacity style={styles.view} onPress={abrirDetalhes}>
      <View style={styles.view3}>
        <Image
          source={{ uri: usuario.imagem }}
          style={styles.imgcontainer}
          resizeMode="cover"
        />
      </View>
      <View style={styles.view2}>
        <View style={styles.iconsContainer}>
          <TouchableOpacity onPress={() => {
            setFavorito(!favorito);
            enviarParaBancoDeDados();
          }}>
            {favorito ? (
              <Icon name="heart" size={18} color="red" />
            ) : (
              <Icon name="heart-o" size={18} color="black" />
            )}
          </TouchableOpacity>
          <View style={styles.estrelasContainer}>
            {[1, 2, 3, 4, 5, 6].map((index) => (
              <TouchableOpacity
                key={index}
                onPress={() => setClassificacao(index)}
                style={styles.estrela}
              >
                <Icon
                  name={index <= classificacao ? 'star' : 'star-o'}
                  size={18}
                  color="gold"
                />
              </TouchableOpacity>
            ))}
          </View>
        </View>
        <Text style={styles.textname}>{usuario.nome}</Text>
        <Text style={styles.textdistancia}> Estado: {usuario.estado}</Text>
        <Text style={styles.textpreco}>Preço por dia: R$ {usuario.preco}</Text>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  view: {
    width: '90%',
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: 'white',
    borderRadius: 15,
    marginVertical: 10,
    marginLeft: '5%',
    marginRight: '5%',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'row',
  },

  view2: {
    flex: 1,
    textAlign: 'center',
    marginLeft: 10,
  },

  view3: {
    width: 120,
    height: 120,
    marginRight: 10,
  },

  imgcontainer: {
    borderWidth: 2,
    borderColor: 'black',
    borderRadius: 100,
    width: '100%',
    height: '100%',
    overflow: 'hidden',
  },

  textname: {
    fontSize: 18,
    fontFamily: 'Ubuntu_700Bold',
  },

  textpreco: {
    fontSize: 15,
    marginTop: 5,
  },

  textdistancia: {
    marginTop: 5,
  },

  iconsContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 5,
  },

  estrelasContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginLeft: 8,
  },

  estrela: {
    marginRight: 5,
  },
});

export default CardUsuario;
