import React, { useState } from "react";
import { View, Text, StyleSheet, StatusBar, SafeAreaView, ImageBackground, TextInput, TouchableOpacity } from "react-native";
import { ScrollView } from "react-native-gesture-handler";
import { Ionicons } from "react-native-vector-icons";
import { useNavigation } from '@react-navigation/native';

import ListaDeUsuarios from "../ListaDeUsuarios/ListaDeUsuarios";
import styleGeral from '../estiloGeral/style'

export default function tPesquisa() {

    const [text, setText] = useState("");
    const navigation = useNavigation();

    function Voltar() {
        navigation.goBack()
    }

    const handleSearch = () => {
      // Chame a função de filtro do componente ListaDeUsuarios passando o texto da pesquisa
      ListaDeUsuarios.filterUsuarios(text);
    };

    return (
        <ImageBackground style={styles.container}>
            <SafeAreaView style={styles.view}>
                <View style={styles.cabecalho}>
                    <Ionicons
                        name="chevron-back"
                        size={40}
                        color="black"
                        onPress={Voltar}    
                    />
                    <TextInput
                        style={styles.input}
                        placeholder="Pesquisar"
                        autoCapitalize="none"
                        autoCorrect={false}
                        value={text}
                        onChangeText={(value) => setText(value)}
                    />
                    <TouchableOpacity onPress={handleSearch}>
                        <Ionicons
                            name="search"
                            size={40}
                            color="black"
                        />
                    </TouchableOpacity>
                </View>
            </SafeAreaView>

            <ScrollView>
                <View style={styles.container}>
                    <ListaDeUsuarios/>
                </View>
            </ScrollView>

        </ImageBackground>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: StatusBar.currentHeight,
        padding: 10,
        backgroundColor: "white"
    },
    cabecalho: {
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between",
        marginBottom: 10,
    },
    input: {
        flex: 1,
        height: 40,
        backgroundColor: "#f2f2f2",
        borderRadius: 10,
        paddingHorizontal: 10,
        marginLeft: 10,
        marginRight: 10,
    },
});
