import React, { useState, useEffect } from 'react';
import { View, Image, ImageBackground, StyleSheet, Text, TextInput } from 'react-native';
import { Feather as Icon } from '@expo/vector-icons';
import { RectButton } from 'react-native-gesture-handler';
import { useNavigation, useRoute } from '@react-navigation/native';
import { Ionicons } from "react-native-vector-icons";

import styles from './estilo';

export default function tDetalhes() {

    const navigation = useNavigation();

    function Voltar() {
        navigation.goBack()
    }

    const route = useRoute();
    const id = route?.params?.params?.id;

    const [usuario, setUsuario] = useState(null);

    useEffect(() => {
        fetch(`https://appcuidadores.azurewebsites.net/api/cuidadores?id=${id}`, {
            method: "GET",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
        })
            .then((response) => response.json())
            .then((json) => {
                setUsuario(json);
            })
            .catch((error) => {
                console.error(error);
                alert("Erro ao buscar usuário");
            });
    }, [id]);

    if (!usuario) {
        return (
            <View style={styles.container}>
                <Text>Carregando...</Text>
            </View>
        );
    }

    return (
        <ImageBackground>
            <View>
                <Text>{usuario.id}</Text>
                <Text>{usuario.email}</Text>
                <Text>{usuario.celular}</Text>
                <Text>{usuario.descricao}</Text>
            </View>
        </ImageBackground>
    );
}
