import React from 'react';
import { View, StyleSheet, Image, TouchableOpacity } from "react-native";
import {Text } from 'react-native-web';
import { useNavigation } from '@react-navigation/native';


function CardUsuario({ usuario }) {

    const navigation = useNavigation()

    function abrirDetalhes(id) {
        navigation.navigate('tDetalhes', { id })
    }    
   

    return (
        <TouchableOpacity style={styles.view} onPress={() => abrirDetalhes(usuario.id)}>

            <View style={styles.view2}>
                <Text>{usuario.nome}</Text>
                <Text>{usuario.email}</Text>
                <Text>{usuario.celular}</Text>
                <Text>{usuario.descricao}</Text>
            </View>

            <View style={styles.view3}>
                <Image source={{ uri: usuario.imagem }} style={styles.imgcontainer} />
            </View>
        </TouchableOpacity>

    );
}

export default CardUsuario;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#FFB6C1'
    },

    view: {
        padding: 20,
        backgroundColor: 'white',
        borderRadius: 15,
        marginVertical: 10,
        marginLeft: 20,
        marginRight: 20
    },

    view2: {
        marginLeft: 100,
        textAlign: 'center'
    },

    view3: {
        alignItems: 'flex-start'
    },

    imgcontainer: {
        border: 200,
        borderColor: 'Black',
        borderRadius: 20,
        marginRight: 210,
        marginTop: -115,
        width: 110,
        height: 110
    },

    texto: {
        textAlign: 'center'
    }
});
