import React, { createContext, useState, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import jwtDecode from 'jwt-decode';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    verificarLogin();
  }, []);

  async function verificarLogin() {
    const usuarioLogado = await ChecarLoginUsuario();
    if (usuarioLogado) {
      // Usuário está logado, definir o usuário no estado
      setUser(usuarioLogado);
    }
  }

  async function SalvarJWT(jwtData) {
    const userData = jwtDecode(jwtData);

    await AsyncStorage.setItem('@jwt', jwtData);
    await AsyncStorage.setItem('@userData', JSON.stringify(userData));

    // Atualizar o usuário no estado
    setUser(userData);
  }

  async function HeaderRequisicao() {
    const usuarioLogado = await ChecarLoginUsuario();

    if (!usuarioLogado) {
      // Usuário não está logado, lidar com a lógica apropriada aqui (redirecionar, mostrar mensagem, etc.)
      return null;
    }

    const token = await AsyncStorage.getItem('@jwt');
    return new Headers({
      Authorization: 'Bearer ' + token,
      'Content-Type': 'application/json',
    });
  }

  async function ChecarLoginUsuario() {
    const token = await AsyncStorage.getItem('@jwt');
    if (!token) {
      return null;
    }

    const userData = JSON.parse(await AsyncStorage.getItem('@userData'));
    const actualDate = Date.now() / 1000;

    if (actualDate > userData.exp) {
      // Usuário expirado, lidar com a lógica apropriada aqui (remover token, redirecionar, mostrar mensagem, etc.)
      await AsyncStorage.removeItem('@jwt');
      await AsyncStorage.removeItem('@userData');
      return null;
    }

    return userData;
  }

  return (
    <AuthContext.Provider value={{ user, SalvarJWT, HeaderRequisicao, ChecarLoginUsuario }}>
      {children}
    </AuthContext.Provider>
  );
};
