import React, { useState } from 'react';
import { View, StyleSheet, Image, TouchableOpacity, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { DadosUsuario, HeaderRequisicao } from '../../AuthContext';

function CardUsuario({ usuario }) {
  const navigation = useNavigation();
  const [favorito, setFavorito] = useState(false);
  const [classificacao, setClassificacao] = useState(0);

  const abrirDetalhes = () => {
    navigation.navigate('tDetalhes', { usuario });
  };

  const adicionar = async () => {
    const cuidadorId = usuario.id; // ID do cuidador
    const userData = await DadosUsuario(); // ID do usuário do aplicativo (você pode substituir pelo valor correto)
    const url = `https://localhost:44396/api/favoritosusu`;

    const headers = await HeaderRequisicao();

    fetch(url, { method: 'POST', })
      .then((response) => {
        if (response.ok) {
          // Sucesso ao adicionar os dados da API
          console.log('Dados adicionados com sucesso da API');
        } else {
          // Erro ao adicionar os dados da API
          throw new Error('Erro ao adicionar os dados da API');
        }
      })
      .catch((error) => {
        console.log(error);
        alert('Erro ao adicionar informações');
      });
  };

  const handleToggleFavorito = () => {
    setFavorito((prevFavorito) => !prevFavorito);

    if (!favorito) {
      adicionar();
    }
  };

  const renderEstrelas = () => {
    return [1, 2, 3, 4, 5].map((index) => (
      <TouchableOpacity
        key={index}
        onPress={() => setClassificacao(index)}
        style={styles.estrela}
      >
        <Icon
          name={index <= classificacao ? 'star' : 'star-o'}
          size={18}
          color="gold"
        />
      </TouchableOpacity>
    ));
  };

  return (
    <TouchableOpacity style={styles.cardContainer} onPress={abrirDetalhes}>
      <Image
        source={{ uri: usuario.imagem }}
        style={styles.image}
        resizeMode="cover"
      />
      <View style={styles.infoContainer}>
        <Text style={styles.nameText}>{usuario.nome}</Text>
        <View style={styles.tipo}>
          <Text style={styles.tipoText}>{usuario.tipo}</Text>
        </View>
        <Text style={styles.distanciaText}>Estado: {usuario.estado}</Text>
        <Text style={styles.precoText}>Preço por dia: R$ {usuario.preco}</Text>
        <View style={styles.upperContainer}>
          <TouchableOpacity onPress={handleToggleFavorito}>
            {favorito ? (
              <Icon name="heart" size={18} color="red" />
            ) : (
              <Icon name="heart-o" size={18} color="black" />
            )}
          </TouchableOpacity>
          <View style={styles.estrelasContainer}>{renderEstrelas()}</View>
        </View>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cardContainer: {
    width: '90%',
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: 'white',
    borderRadius: 15,
    marginVertical: 10,
    marginLeft: '5%',
    marginRight: '5%',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'row',
  },

  upperContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginBottom: 10,
  },

  estrelasContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },

  estrela: {
    marginLeft: 5,
  },

  image: {
    borderWidth: 2,
    borderColor: 'black',
    borderRadius: 100,
    width: 120,
    height: 120,
    overflow: 'hidden',
  },
  
  infoContainer: {
    marginLeft: 10,
  },

  nameText: {
    fontSize: 18,
    fontFamily: 'Ubuntu_700Bold',
    marginBottom: 5,
  },

  distanciaText: {
    fontSize: 15,
    marginBottom: 5,
  },

  precoText: {
    fontSize: 15,
    marginBottom: 5,
  },

  tipo: {
    backgroundColor: 'lightgreen',
    borderRadius: 10,
    paddingHorizontal: 10,
    paddingVertical: 5,
    alignSelf: 'flex-start',
    marginTop: 5,
  },

  tipoText: {
    fontSize: 12,
    color: 'black',
  },
});

export default CardUsuario;
