import React, { useEffect, useState } from 'react';
import { View } from 'react-native';

import { HeaderRequisicao } from '../../AuthContext';
import CardUsuario from '../../components/Cards/CardUsuario';

function ListaDeUsuarios({ searchTerm }) {
  const [usuarios, setUsuarios] = useState([]);

  async function ListarCuidadores() {
    const headers = await HeaderRequisicao();

    fetch('https://cuidadores.azurewebsites.net/api/cuidadores', {
      method: 'GET',
      headers,
    })
      .then((response) => response.json())
      .then((json) => {
        let filteredUsuarios = json;

        if (searchTerm) {
          const searchValues = searchTerm.split(' ');
          const searchName = searchValues[0] || '';
          const searchPrice = searchValues[1] || '';
          const searchState = searchValues[2] || '';
          
          if (searchName) {
            filteredUsuarios = filteredUsuarios.filter((usuario) =>
              usuario.nome.toLowerCase().includes(searchName.toLowerCase())
            );
          }

          if (searchPrice) {
            filteredUsuarios = filteredUsuarios.filter(
              (usuario) => usuario.preco.toString() === searchPrice
            );
          }
          
          if (searchState) {
            filteredUsuarios = filteredUsuarios.filter(
              (usuario) => usuario.estado.toLowerCase() === searchState.toLowerCase()
            );
          }
          
        }

        setUsuarios(filteredUsuarios);
      })
      .catch((error) => {
        console.log(error);
        alert('Erro ao inserir informações');
      });
  }

  useEffect(() => {
    ListarCuidadores();
  }, []);

  const filteredUsuarios = usuarios.filter((usuario) =>
    usuario.nome.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <View>
      {filteredUsuarios.map((usuario, index) => (
        <CardUsuario usuario={usuario} key={index} />
      ))}
    </View>
  );
}

export default ListaDeUsuarios;
