import React, { useEffect, useState } from 'react';
import { View, ScrollView } from 'react-native';

import { HeaderRequisicao } from '../../AuthContext';
import CardRecente from '../../components/CardUsuario/CardRecente';

function ListaDeRecentes({ searchTerm }) {
  const [recentes, setRecentes] = useState([]);

  async function ListarRecentes() {
    const headers = await HeaderRequisicao();
    fetch('https://localhost:44396/api/recentesusu', {
      method: 'GET',
      headers
    })
      .then((response) => response.json())
      .then((json) => {
        setRecentes(json);
      })
      .catch((error) => {
        console.log(error);
        alert('Erro ao obter informações');
      });
  }
  useEffect(() => {
    ListarRecentes();
  }, []);


  const handleDeleteRecente = (recenteId) => {
    fetch(`https://localhost:44396/api/recentesusu/${recenteId}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then((json) => {
        // Atualizar a lista de recentes após a exclusão
        setRecentes(recentes.filter((recente) => recente.id !== recenteId));
      })
      .catch((error) => {
        console.log(error);
        alert('Erro ao excluir recente');
      });
  };

  return (
    <ScrollView>
      <View>
        {recentes.map((recente, index) => (
          <CardRecente
            usuario={recente.cuidador}
            key={index}
            onDelete={() => handleDeleteRecente(recente.id)}
            favorito={false} // Definindo o favorito como false para manter o coração vazio
          />
        ))}
      </View>
    </ScrollView>
  );
}

export default ListaDeRecentes;
