import React, { useState } from 'react';
import { View, Image, ImageBackground, StyleSheet, Text } from 'react-native';
import { RectButton, TextInput } from 'react-native-gesture-handler';
import { useNavigation } from '@react-navigation/native';
import { useRoute } from '@react-navigation/native';
import * as ImagePicker from 'expo-image-picker';


import styles from './estilo';

export default function tCadastrarFoto() {

    const route = useRoute();
    const [nome, setnome] = useState(route.params?.nome || '');
    // AVISo: se não colocar a data de nascimento no modelo correto o programa não roda
    //   const [data_de_nasc, set_data_de_nasc] = useState(route.params?.data_de_nasc || '');
    const [cpf, setcpf] = useState(route.params?.cpf || '');
    const [celular, setcelular] = useState(route.params?.celular || '');
    const [endereco, setendereco] = useState(route.params?.endereco || '');
    const [email, setemail] = useState(route.params?.email || '');
    const [senha, setsenha] = useState(route.params?.senha || '');

    const [descricao, setdescricao] = useState("");
    const [imagem, setimagem] = useState("");

    const selecionarImagem = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            quality: 1,
        });

        if (result.canceled) {
            return;
        }
        setimagem(result.assets[0].uri);
    }

    function Cadastrar() {

        const body = { nome, cpf, celular, endereco, email, senha, imagem, descricao};

        //fetch("https://appcuidadores.azurewebsites.net/api/cuidadores", {
        fetch("https://localhost:44396/api/cuidadores", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body)
        })
            .then((response) => { alert("Usuário cadastrado com sucesso"); })
            .catch((error) => {
                console.log(error);
                alert("Erro ao cadastrar usuário");
            });
    }

    const navigation = useNavigation();

    const Cadastrar_Avancar = () => {
        Cadastrar();
        Avancao();
    };

    function Avancao() {
        navigation.navigate('tLogin')
    }

    function Voltar() {
        navigation.goBack()
    }

    return (
        <ImageBackground style={styles.container}>

            <View style={styles.main2}>
                <Image source={{ uri: imagem }} style={styles.image} />
            </View>

            <View style={styles.main}>
                <Text style={styles.title}>Selecione sua melhor foto (png, jpg, svg) </Text>
            </View>

            <TextInput
                style={styles.description}
                placeholder="Descrição..."
                multiline
                value={descricao}
                onChangeText={(texto) => setdescricao(texto)}
            />

            <RectButton style={styles.button} onPress={selecionarImagem}>
                <Text style={styles.buttonText}>Escolher Foto</Text>
            </RectButton>

            <RectButton style={styles.button} onPress={Cadastrar_Avancar}>
                <Text style={styles.buttonText}>Avançar</Text>
            </RectButton>

            <RectButton style={styles.buttonReturn} onPress={Voltar}>
                <Text style={styles.buttonText}>Voltar</Text>
            </RectButton>
        </ImageBackground>
    );
}
