import React, { useState, useEffect } from 'react';
import { View, Text } from 'react-native';
import { RectButton, ScrollView, TextInput } from 'react-native-gesture-handler';
import { useNavigation } from '@react-navigation/native';
import * as ImagePicker from 'expo-image-picker'; // Importe a biblioteca ImagePicker

import styles from './estilo';
import { HeaderRequisicao, DadosUsuario } from '../../AuthContext';

function tEditarUsuario() {
    const navigation = useNavigation();

    const [nome, setNome] = useState("");
    const [sobrenome, setSobrenome] = useState("");
    const [data_de_nasc, setdata_de_nasc] = useState("");
    const [cpf, setcpf] = useState("");
    const [sexo, setsexo] = useState("");
    const [celular, setcelular] = useState("");
    const [email, setemail] = useState("");
    const [senha, setsenha] = useState("");
    const [preco, setpreco] = useState("");
    const [link, setlink] = useState("");
    const [cep, setcep] = useState("");
    const [endereco, setendereco] = useState("");
    const [cidade, setcidade] = useState("");
    const [estado, setestado] = useState("");
    const [bairro, setbairro] = useState("");
    const [descricao, setdescricao] = useState("");
    const [imagem, setimagem] = useState(""); // Estado para armazenar a URI da imagem selecionada

    async function MostrarUsuario() {
        const userData = await DadosUsuario();
        const headers = await HeaderRequisicao();
        const url = `https://localhost:44396/api/ConfigUsu?id=${userData.ID}`;

        fetch(url, {
            method: 'GET',
            headers
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                if (data.length > 0) {
                    const usuario = data[0];

                    setNome(usuario.nome || "");
                    setSobrenome(usuario.sobrenome || "");
                    setdata_de_nasc(usuario.data_de_nasc || "");
                    setcpf(usuario.cpf || "");
                    setsexo(usuario.sexo || "");
                    setcelular(usuario.celular || "");
                    setemail(usuario.email || "");
                    setsenha(usuario.senha || "");
                    setpreco(usuario.preco || "");
                    setlink(usuario.link || "");

                    setcep(usuario.cep || "");
                    setendereco(usuario.endereco || "");
                    setcidade(usuario.cidade || "");
                    setestado(usuario.estado || "");
                    setbairro(usuario.bairro || "");

                    setdescricao(usuario.descricao || "");
                    setimagem(usuario.imagem || "");
                }
            })
            .catch((error) => {
                console.log(error);
                alert('Erro ao inserir informações');
            });
    }

    useEffect(() => {
        MostrarUsuario();
    }, []);

    function Navegar() {
        navigation.navigate('Routes2');
    }

    function handleNomeChange(text) {
        setNome(text);
    }

    function handleSobrenomeChange(text) {
        setSobrenome(text);
    }

    function handleDataDeNascChange(text) {
        setdata_de_nasc(text);
    }

    function handleCpfChange(text) {
        setcpf(text);
    }

    function handleSexoChange(text) {
        setsexo(text);
    }

    function handleCelularChange(text) {
        setcelular(text);
    }

    function handleEmailChange(text) {
        setemail(text);
    }

    function handleSenhaChange(text) {
        setsenha(text);
    }

    function handlePrecoChange(text) {
        setpreco(text);
    }

    function handleLinkChange(text) {
        setlink(text);
    }

    function handleCEPChange(text) {
        setcep(text);
    }

    function handleEnderecoChange(text) {
        setendereco(text);
    }

    function handleCidadeChange(text) {
        setcidade(text);
    }

    function handleEstadoChange(text) {
        setestado(text);
    }

    function handleBairroChange(text) {
        setbairro(text);
    }

    function handleDescricaoChange(text) {
        setdescricao(text);
    }

    async function selecionarImagem() {
        const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (!permissionResult.granted) {
            alert('A permissão para acessar a biblioteca de mídia foi negada.');
            return;
        }

        const pickerResult = await ImagePicker.launchImageLibraryAsync();
        if (pickerResult.cancelled) {
            return;
        }

        setimagem(pickerResult.uri); // Armazena a URI da imagem selecionada
    }

    return (
        <ScrollView>
            <View style={styles.container}>
                <Text style={styles.title}>Olá, Usuário!</Text>
                <View style={styles.view}>
                    <Text style={styles.title}>Informações pessoais</Text>
                    <Text style={styles.text}>Nome</Text>
                    <TextInput
                        style={styles.input}
                        value={nome}
                        onChangeText={handleNomeChange}
                    />

                    <Text style={styles.text}>Sobrenome</Text>
                    <TextInput
                        style={styles.input}
                        value={sobrenome}
                        onChangeText={handleSobrenomeChange}
                    />

                    <Text style={styles.text}>Data de Nascimento</Text>
                    <TextInput
                        style={styles.input}
                        value={data_de_nasc}
                        onChangeText={handleDataDeNascChange}
                    />

                    <Text style={styles.text}>CPF</Text>
                    <TextInput
                        style={styles.input}
                        value={cpf}
                        onChangeText={handleCpfChange}
                    />

                    <Text style={styles.text}>Sexo</Text>
                    <TextInput
                        style={styles.input}
                        value={sexo}
                        onChangeText={handleSexoChange}
                    />

                    <Text style={styles.text}>Celular</Text>
                    <TextInput
                        style={styles.input}
                        value={celular}
                        onChangeText={handleCelularChange}
                    />

                    <Text style={styles.text}>Email</Text>
                    <TextInput
                        style={styles.input}
                        value={email}
                        onChangeText={handleEmailChange}
                    />

                    <Text style={styles.text}>Senha</Text>
                    <TextInput
                        style={styles.input}
                        value={senha}
                        onChangeText={handleSenhaChange}
                    />

                    <Text style={styles.text}>Preço</Text>
                    <TextInput
                        style={styles.input}
                        value={preco}
                        onChangeText={handlePrecoChange}
                    />

                    <Text style={styles.text}>Link</Text>
                    <TextInput
                        style={styles.input}
                        value={link}
                        onChangeText={handleLinkChange}
                    />

                    <Text style={styles.text}>Descrição</Text>
                    <TextInput
                        style={styles.inputDesc}
                        multiline
                        value={descricao}
                        onChangeText={handleDescricaoChange}
                    />

                    <Text style={styles.title}>Localidade</Text>
                    <Text style={styles.text}>CEP</Text>
                    <TextInput
                        style={styles.input}
                        value={cep}
                        onChangeText={handleCEPChange}
                    />

                    <Text style={styles.text}>Endereço</Text>
                    <TextInput
                        style={styles.input}
                        value={endereco}
                        onChangeText={handleEnderecoChange}
                    />

                    <Text style={styles.text}>Cidade</Text>
                    <TextInput
                        style={styles.input}
                        value={cidade}
                        onChangeText={handleCidadeChange}
                    />

                    <Text style={styles.text}>Estado</Text>
                    <TextInput
                        style={styles.input}
                        value={estado}
                        onChangeText={handleEstadoChange}
                    />

                    <Text style={styles.text}>Bairro</Text>
                    <TextInput
                        style={styles.input}
                        value={bairro}
                        onChangeText={handleBairroChange}
                    />

                    <Text style={styles.text}>Imagem</Text>
                    <RectButton style={styles.button} onPress={selecionarImagem}>
                        <Text style={styles.buttonText}>Selecionar Imagem</Text>
                    </RectButton>
                    {imagem !== "" && (
                        <Image
                            source={{ uri: imagem }}
                            style={styles.imagem}
                        />
                    )}

                    <RectButton style={styles.button} onPress={Navegar}>
                        <Text style={styles.buttonText}>Salvar Alterações</Text>
                    </RectButton>
                </View>
            </View>
        </ScrollView>
    );
}

export default tEditarUsuario;
