import React, { useEffect, useState } from 'react';
import { View, Text, TextInput, ScrollView } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { useRoute, useNavigation } from '@react-navigation/native';

import styles from './estilo';

import { HeaderRequisicao, DadosUsuario } from '../../AuthContext';

function TConfiguracao() {
  const navigation = useNavigation();

  function Avancao() {
    navigation.navigate('TConfirmarSenha');
  }

  const route = useRoute();

  const [nome, setNome] = useState("");
  const [sobrenome, setSobrenome] = useState("");
  const [email, setEmail] = useState("");
  const [cidade, setCidade] = useState("");
  const [celular, setCelular] = useState("");
  const [preco, setPreco] = useState("");
  const [descricao, setDescricao] = useState("");

  const [dadosCarregados, setDadosCarregados] = useState(false);

  async function MostrarUsuario() {
    try {
      const userData = await DadosUsuario();
      const headers = await HeaderRequisicao();
      const url = `https://localhost:44396/api/ConfigUsu?id=${userData.ID}`;

      const response = await fetch(url, {
        method: 'GET',
        headers
      });

      if (response.ok) {
        const usuario = await response.json();
        setNome(usuario.nome);
        setSobrenome(usuario.sobrenome);
        setEmail(usuario.email);
        setCidade(usuario.cidade);
        setCelular(usuario.celular);
        setPreco(usuario.preco);
        setDescricao(usuario.descricao);
        setDadosCarregados(true);
      } else {
        console.log(response);
        alert('Erro ao inserir informações');
      }
    } catch (error) {
      console.log(error);
      alert('Erro ao inserir informações');
    }
  }

  useEffect(() => {
    MostrarUsuario();
  }, []);

  return (
    <ScrollView>
      <View style={styles.container}>
        <Text style={styles.title}>Olá, Usuário!</Text>
        <View style={styles.view}>
          <View style={styles.editor}>
            <FontAwesome
              name="pencil-square-o"
              size={24}
              color="black"
              onPress={Avancao}
            />
          </View>

          {dadosCarregados && (
            <>
              <Text style={styles.text}>Nome</Text>
              <TextInput
                style={styles.input}
                value={nome}
                onChangeText={setNome}
              />

              <Text style={styles.text}>Sobrenome</Text>
              <TextInput
                style={styles.input}
                value={sobrenome}
                onChangeText={setSobrenome}
              />

              <Text style={styles.text}>Email</Text>
              <TextInput
                style={styles.input}
                value={email}
                onChangeText={setEmail}
              />

              <Text style={styles.text}>Cidade</Text>
              <TextInput
                style={styles.input}
                value={cidade}
                onChangeText={setCidade}
              />

              <Text style={styles.text}>Celular</Text>
              <TextInput
                style={styles.input}
                value={celular}
                onChangeText={setCelular}
              />

              <Text style={styles.text}>Preço</Text>
              <TextInput
                style={styles.input}
                value={preco}
                onChangeText={setPreco}
              />

              <Text style={styles.text}>Descrição</Text>
              <TextInput
                style={styles.inputDesc}
                value={descricao}
                onChangeText={setDescricao}
                multiline
              />
            </>
          )}
        </View>
      </View>
    </ScrollView>
  );
}

export default TConfiguracao;
