import React, { useState } from 'react';
import { View, Image, ImageBackground, StyleSheet, Text, TextInput, ActivityIndicator } from 'react-native';
import { Feather as Icon } from '@expo/vector-icons';
import { RectButton } from 'react-native-gesture-handler';
import { useNavigation, useRoute } from '@react-navigation/native';
import { Ionicons } from "react-native-vector-icons";

import styles from './estilo';

export default function tDetalhes() {
    
    const navigation = useNavigation();
    const route = useRoute();
    const { usuario } = route.params;

    const [isLoading, setIsLoading] = useState(true);

    function voltar() {
        navigation.goBack()
    }

    return (
        <View style={styles.container}>
            <Ionicons
                name="chevron-back"
                size={40}
                color="black"
                onPress={voltar}    
            />
            <Text style={styles.title}>Detalhes do usuário:</Text>
            <Text>Nome: {usuario.nome}</Text>
            <Text>Email: {usuario.email}</Text>
            <Text>Celular: {usuario.celular}</Text>
            <Text>Descrição: {usuario.descricao}</Text>
            <Text>Preço: {usuario.preco}</Text>
            <Text>Link: {usuario.link}</Text>
            <Text>Enderço: {usuario.endereco}</Text>

            <Text>Imagem:</Text>

            <Image
                style={styles.image}
                source={{ uri: usuario.imagem }}
                onLoadStart={() => setIsLoading(true)}
                onLoadEnd={() => setIsLoading(false)}
            />
        </View>
    );
}
