import React, { useState, useEffect } from 'react';
import { View, Image, ImageBackground, StyleSheet, Text, TextInput } from 'react-native';
import { Feather as Icon } from '@expo/vector-icons';
import { RectButton } from 'react-native-gesture-handler';
import { useNavigation, useRoute } from '@react-navigation/native';
import { Ionicons } from "react-native-vector-icons";

import styles from './estilo';

export default function tDetalhes() {

    const navigation = useNavigation();

    function Voltar() {
        navigation.goBack()
    }
    
    const route = useRoute();
    const { id } = route.params;

    const [usuario, setUsuario] = useState(null);


    useEffect(() => {
        async function fetchUsuario() {
            const response = await fetch(`https://localhost:44396/api/cuidadores${id}`);
            const data = await response.json();
            setUsuario(data);
        }

        fetchUsuario();
    }, [id]);

    if (!usuario) {
        return (
            <View style={styles.container}>
                <Text>Carregando...</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <Text>{usuario.nome}</Text>
            <Text>{usuario.email}</Text>
            <Text>{usuario.celular}</Text>
            <Text>{usuario.descricao}</Text>
            <Text>{usuario.preco}</Text>
            <Text>{usuario.estado}</Text>
        </View>
    );
}
